CREATE TABLE exact_matches_canonical_name AS (SELECT o.osm_id, o.osm_type, ST_AsGeojson(ST_Transform(o.the_geom, 4326)) as osm_geojson, m.qid, admin, name, name_en, label_en, label_zh, score, ST_Distance(the_geom, geom) as distance, ST_AsGeojson(ST_Transform(geom, 4326)) as wiki_geojson FROM osm o, mb_wikidata m WHERE m.label_en=o.canonical_name);